window.mydChangeTab=function(t){t.preventDefault();let e=document.querySelectorAll(".myd-tab"),a=document.querySelectorAll(".myd-tabs-content"),c=t.target;e.forEach((t=>{t.classList.remove("nav-tab-active")})),a.forEach((t=>{t.classList.remove("myd-tabs-content--active")})),c.classList.add("nav-tab-active"),document.getElementById(c.id+"-content").classList.add("myd-tabs-content--active")};